// swiftlint:disable force_unwrapping
class HMTLParserTests: XCTestCase {

    func testThatItDecodesHtmlEntities() throws {
        let cases = [
            "&amp;": NSAttributedString(string: "&"),
            "&amp;&amp;": NSAttributedString(string: "&&"),
            "&amp; &amp;": NSAttributedString(string: "& &"),
            "&quot;Fox&quot;": NSAttributedString(string: "\"Fox\""),
            "gr&Egrave;en": NSAttributedString(string: "gr√àen"),
            "<a href=\"&amp;\">a</a>": NSAttributedString(string: "a", attributes: [.link: URL(string: "&")!]),
            "a<br>b": NSAttributedString(string: "a\nb"),
            "a<br/>b": NSAttributedString(string: "a\nb"),
            "<div><a href=\"https://www.amboss.com/de/library#xid=Qt0uW3&amp;anker=Z44e\"><span>Look &amp; cl&icirc;ck h&Ecirc;re</span></a></div>": NSAttributedString(string: "Look & cl√Æck h√äre", attributes: [.link: URL(string: "https://www.amboss.com/de/library#xid=Qt0uW3&anker=Z44e")!])
        ]

        for (input, expected) in cases {
            let parsed = try HTMLParser.attributedStringFromHTML(htmlString: input)
            XCTAssertEqual(parsed.description, expected.description, "expected \(expected) but found \(parsed)")
        }
    }

    func testThatItAssignsFontsAsExpected() throws {

        let size = UIFont.systemFontSize
        let normal = UIFont.systemFont(ofSize: size)
        let bold = UIFont.boldSystemFont(ofSize: size)
        let italic = UIFont.italicSystemFont(ofSize: size)

        let style = HTMLParser.Attributes(normal: [.font: normal], bold: [.font: bold], italic: [.font: italic])

        let cases = [
            "This is normal text": NSAttributedString(string: "This is normal text", attributes: [.font: normal]),
            "<b>This is bold text</b>": NSAttributedString(string: "This is bold text", attributes: [.font: bold]),
            "<i>This text is italic</i>": NSAttributedString(string: "This text is italic", attributes: [.font: italic])
        ]

        for (input, expected) in cases {
            let parsed = try HTMLParser.attributedStringFromHTML(htmlString: input, with: style)
            XCTAssertEqual(parsed.description, expected.description, "expected \(expected) but found \(parsed)")
        }
    }

    func testThatItWorksFineWithEmojis() throws {
        let cases = [
            "üòè": NSAttributedString(string: "üòè"),
            "<div>I am an emoji üòè</div>": NSAttributedString(string: "I am an emoji üòè"),
            "<a href=\"foo\">üòè</a>": NSAttributedString(string: "üòè", attributes: [.link: URL(string: "foo")!])
        ]

        for (input, expected) in cases {
            let parsed = try HTMLParser.attributedStringFromHTML(htmlString: input)
            XCTAssertEqual(parsed.description, expected.description, "expected \(expected) but found \(parsed)")
        }
    }

    func testThatOtherHtmlTagsAreIgnored() throws {
        let cases = [
            "<b>b</b>": NSAttributedString(string: "b"),
            "<i>i</i>": NSAttributedString(string: "i")
        ]

        for (input, expected) in cases {
            let parsed = try HTMLParser.attributedStringFromHTML(htmlString: input)
            XCTAssertEqual(parsed.description, expected.description, "expected \(expected) but found \(parsed)")
        }
    }

    func testThatTrailingParagraphsAreTrimmed() throws {
        let cases = [
            "<div>a</div><div>b</div><div></div>": NSAttributedString(string: "a\nb")
        ]

        for (input, expected) in cases {
            let parsed = try HTMLParser.attributedStringFromHTML(htmlString: input)
            XCTAssertEqual(parsed.description, expected.description, "expected \(expected) but found \(parsed)")
        }
    }

    func testThatEmptyElementsStillParse() throws {
        let cases = [
            "": NSAttributedString(string: ""),
            "I am just text ": NSAttributedString(string: "I am just text"),
            "<p></p>": NSAttributedString(string: ""),
            "<div></div>": NSAttributedString(string: ""),
            "<p><div></div></p>": NSAttributedString(string: ""),
            "<div><p></p></div>": NSAttributedString(string: "")
        ]

        for (input, expected) in cases {
            let parsed = try HTMLParser.attributedStringFromHTML(htmlString: input)
            XCTAssertEqual(parsed.description, expected.description, "expected \(expected) but found \(parsed)")
        }
    }

    func testInvalidItemsStillParse() throws {
        XCTAssertEqual(try HTMLParser.attributedStringFromHTML(htmlString: BrokenFeed.itemWithMissingDivTag).length, 443)
